select address_id, address_province, address_city, address_name from pls.AddressInfo
select count(1) from AddressInfo
and address_id = #{addressId}
and address_province = #{addressProvince}
and address_city = #{addressCity}
and address_name = #{addressName}
insert into AddressInfo(address_province, address_city, address_name) values (#{addressProvince}, #{addressCity}, #{addressName})
insert into AddressInfo(address_province, address_city, address_name) values
(#{entity.addressProvince}, #{entity.addressCity}, #{entity.addressName})
insert into AddressInfo(address_province, address_city, address_name) values
(#{entity.addressProvince}, #{entity.addressCity}, #{entity.addressName})
on duplicate key update address_province = values(address_province), address_city = values(address_city), address_name = values(address_name)
update AddressInfo
address_province = #{addressProvince},
address_city = #{addressCity},
address_name = #{addressName},
where address_id = #{addressId}
delete from AddressInfo where address_id = #{addressId}
