mapper.select:
select
  ad_id, employee_id, ad_content, ad_money, ad_adviser, ad_start, ad_end, ad_images
from AdvertisementInfo


        and ad_id = #{adId}


        and employee_id = #{employeeId}


        and ad_content = #{adContent}


        and ad_money = #{adMoney}


        and ad_adviser = #{adAdviser}


        and ad_start = #{adStart}


        and ad_end = #{adEnd}


        and ad_images = #{adImages}


limit #{pageable.offset}, #{pageable.pageSize}

select ad_id, employee_id, ad_content, ad_money, ad_adviser, ad_start, ad_end, ad_images from pls.AdvertisementInfo
select count(1) from AdvertisementInfo
and ad_id = #{adId}
and employee_id = #{employeeId}
and ad_content = #{adContent}
and ad_money = #{adMoney}
and ad_adviser = #{adAdviser}
and ad_start = #{adStart}
and ad_end = #{adEnd}
and ad_images = #{adImages}
insert into AdvertisementInfo(employee_id, ad_content, ad_money, ad_adviser, ad_start, ad_end, ad_images) values (#{employeeId}, #{adContent}, #{adMoney}, #{adAdviser}, #{adStart}, #{adEnd}, #{adImages})
insert into AdvertisementInfo(employee_id, ad_content, ad_money, ad_adviser, ad_start, ad_end, ad_images) values
(#{entity.employeeId}, #{entity.adContent}, #{entity.adMoney}, #{entity.adAdviser}, #{entity.adStart}, #{entity.adEnd}, #{entity.adImages})
insert into AdvertisementInfo(employee_id, ad_content, ad_money, ad_adviser, ad_start, ad_end, ad_images) values
(#{entity.employeeId}, #{entity.adContent}, #{entity.adMoney}, #{entity.adAdviser}, #{entity.adStart}, #{entity.adEnd}, #{entity.adImages})
on duplicate key update employee_id = values(employee_id), ad_content = values(ad_content), ad_money = values(ad_money), ad_adviser = values(ad_adviser), ad_start = values(ad_start), ad_end = values(ad_end), ad_images = values(ad_images)
update AdvertisementInfo
employee_id = #{employeeId},
ad_content = #{adContent},
ad_money = #{adMoney},
ad_adviser = #{adAdviser},
ad_start = #{adStart},
ad_end = #{adEnd},
ad_images = #{adImages},
where ad_id = #{adId}
delete from AdvertisementInfo where ad_id = #{adId}
