(#{entity.carId}, #{entity.fixStarttime}, #{entity.fixEndtime}, #{entity.fixName}, #{entity.fixMoney})
on duplicate key update car_id = values(car_id), fix_starttime = values(fix_starttime), fix_endtime = values(fix_endtime), fix_name = values(fix_name), fix_money = values(fix_money)
update FixInfo
