mapper.select:
select
  merchandise_id, build_id, purchasesale_id, merchandise_name, merchandise_type, repository_count, merchandise_height, merchandise_size, merchandise_supplier, merchandise_count
from MerchandiseInfo


        and merchandise_id = #{merchandiseId}


        and build_id = #{buildId}


        and purchasesale_id = #{purchasesaleId}


        and merchandise_name = #{merchandiseName}


        and merchandise_type = #{merchandiseType}


        and repository_count = #{repositoryCount}


        and merchandise_height = #{merchandiseHeight}


        and merchandise_size = #{merchandiseSize}


        and merchandise_supplier = #{merchandiseSupplier}


        and merchandise_count = #{merchandiseCount}


limit #{pageable.offset}, #{pageable.pageSize}

select merchandise_id, build_id, purchasesale_id, merchandise_name, merchandise_type, repository_count, merchandise_height, merchandise_size, merchandise_supplier, merchandise_count from pls.MerchandiseInfo
select count(1) from MerchandiseInfo
and merchandise_id = #{merchandiseId}
and build_id = #{buildId}
and purchasesale_id = #{purchasesaleId}
and merchandise_name = #{merchandiseName}
and merchandise_type = #{merchandiseType}
and repository_count = #{repositoryCount}
and merchandise_height = #{merchandiseHeight}
and merchandise_size = #{merchandiseSize}
and merchandise_supplier = #{merchandiseSupplier}
and merchandise_count = #{merchandiseCount}
insert into MerchandiseInfo(build_id, purchasesale_id, merchandise_name, merchandise_type, repository_count, merchandise_height, merchandise_size, merchandise_supplier, merchandise_count) values (#{buildId}, #{purchasesaleId}, #{merchandiseName}, #{merchandiseType}, #{repositoryCount}, #{merchandiseHeight}, #{merchandiseSize}, #{merchandiseSupplier}, #{merchandiseCount})
insert into MerchandiseInfo(build_id, purchasesale_id, merchandise_name, merchandise_type, repository_count, merchandise_height, merchandise_size, merchandise_supplier, merchandise_count) values
(#{entity.buildId}, #{entity.purchasesaleId}, #{entity.merchandiseName}, #{entity.merchandiseType}, #{entity.repositoryCount}, #{entity.merchandiseHeight}, #{entity.merchandiseSize}, #{entity.merchandiseSupplier}, #{entity.merchandiseCount})
insert into MerchandiseInfo(build_id, purchasesale_id, merchandise_name, merchandise_type, repository_count, merchandise_height, merchandise_size, merchandise_supplier, merchandise_count) values
(#{entity.buildId}, #{entity.purchasesaleId}, #{entity.merchandiseName}, #{entity.merchandiseType}, #{entity.repositoryCount}, #{entity.merchandiseHeight}, #{entity.merchandiseSize}, #{entity.merchandiseSupplier}, #{entity.merchandiseCount})
on duplicate key update build_id = values(build_id), purchasesale_id = values(purchasesale_id), merchandise_name = values(merchandise_name), merchandise_type = values(merchandise_type), repository_count = values(repository_count), merchandise_height = values(merchandise_height), merchandise_size = values(merchandise_size), merchandise_supplier = values(merchandise_supplier), merchandise_count = values(merchandise_count)
update MerchandiseInfo
build_id = #{buildId},
purchasesale_id = #{purchasesaleId},
merchandise_name = #{merchandiseName},
merchandise_type = #{merchandiseType},
repository_count = #{repositoryCount},
merchandise_height = #{merchandiseHeight},
merchandise_size = #{merchandiseSize},
merchandise_supplier = #{merchandiseSupplier},
merchandise_count = #{merchandiseCount},
where merchandise_id = #{merchandiseId}
delete from MerchandiseInfo where merchandise_id = #{merchandiseId}
