select purchasesale_id, purchasesale_type, purchasesale_price, purchasesale_count from pls.PurchaseSale
select count(1) from PurchaseSale
and purchasesale_id = #{purchasesaleId}
and purchasesale_type = #{purchasesaleType}
and purchasesale_price = #{purchasesalePrice}
and purchasesale_count = #{purchasesaleCount}
insert into PurchaseSale(purchasesale_type, purchasesale_price, purchasesale_count) values (#{purchasesaleType}, #{purchasesalePrice}, #{purchasesaleCount})
insert into PurchaseSale(purchasesale_type, purchasesale_price, purchasesale_count) values
(#{entity.purchasesaleType}, #{entity.purchasesalePrice}, #{entity.purchasesaleCount})
insert into PurchaseSale(purchasesale_type, purchasesale_price, purchasesale_count) values
(#{entity.purchasesaleType}, #{entity.purchasesalePrice}, #{entity.purchasesaleCount})
on duplicate key update purchasesale_type = values(purchasesale_type), purchasesale_price = values(purchasesale_price), purchasesale_count = values(purchasesale_count)
update PurchaseSale
purchasesale_type = #{purchasesaleType},
purchasesale_price = #{purchasesalePrice},
purchasesale_count = #{purchasesaleCount},
where purchasesale_id = #{purchasesaleId}
delete from PurchaseSale where purchasesale_id = #{purchasesaleId}
